import React from "react";
import { Text, View } from "react-native";

class HoverableText extends React.Component {
  setStyles = (styles) => {
    this.root.setNativeProps({
      style: styles,
    });
  };

  render() {
    const {
      onHoverText,
      onHoverView,
      textStyle,
      viewStyle,
      ...passThrough
    } = this.props;
    return (
      <View
        ref={(component) => {
          this.root = component;
        }}
        onMouseEnter={() => this.setStyles(onHoverView)}
        onMouseLeave={() => this.setStyles(viewStyle)}
      >
        <Text
          ref={(component) => {
            this.root = component;
          }}
          onMouseEnter={() => this.setStyles(onHoverText)}
          onMouseLeave={() => this.setStyles(textStyle)}
          style={style}
          {...passThrough}
        />
      </View>
    );
  }
}

export default HoverableText;
