import React from "react";
import { StyleSheet, View, Text, Image } from "react-native";
import HoverableView from "./HoverableView";
import HoverableText from "./HoverableText";

const DAOlogoheader = () => (
  <Image
    style={styles.daologoheader}
    source={require("./assets/img/DAOtutorlogotest3.png")}
  />
);
class TopView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ishover: false,
      thiscolor: "black",
    };
    this.handlehoverchange = this.handlehoverchange.bind(this);
  }
  handlehoverchange = () => {
    console.log("hello");
  };

  componentDidUpdate() {
    if (this.state.ishover) {
      this.setState({
        thiscolor: "purple",
      });
    } else {
      this.setState({
        thiscolor: "black",
      });
    }
  }
  render() {
    return (
      <View style={styles.topInfo}>
        <View style={styles.daotutortitle}>
          <DAOlogoheader />
          <Text style={styles.daotutorstext}>DAO Tutors</Text>
        </View>
        <View style={styles.daoheadempty} />
        <View style={styles.daoheaderlinks}>
          <HoverableView
            onHover={styles.hoverTest}
            style={styles.hoverNot}
            onPress={() => this.handlehoverchange}
          >
            <HoverableText style={styles.beans} onHover={styles.hoverNot}>
              BEANS
            </HoverableText>
          </HoverableView>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  topInfo: {
    height: 100,
    justifyContent: "flex-start",
    backgroundColor: "#764ABC",
    flexDirection: "row",
    alignContent: "center",
  },

  daotutortitle: {
    flexDirection: "row",
    justifyContent: "flex-start",

    flex: 4,
  },
  daoheadempty: {
    flex: 2,
  },
  daoheaderlinks: {
    flex: 4,
  },
  beans: {
    fontFamily: "Manrope-SemiBold",
    color: "white",
  },
  noBeans: {
    fontFamily: "Manrope-SemiBold",
    color: "purple",
  },
  daotutorstext: {
    paddingLeft: 70,
    fontFamily: "Manrope-ExtraBold",
    fontSize: 40,
    color: "white",
    alignSelf: "center",
  },
  daologoheader: {
    left: 50,
    height: 70,
    width: 70,
    alignSelf: "center",
  },
  hoverTest: {
    backgroundColor: "white",
    color: "blue",
  },
  hoverNot: {
    backgroundColor: "transparent",
    color: "red",
  },
});

export default TopView;
