import React from "react";
import { Text } from "react-native";

class HoverableText extends React.Component {
  setStyles = (styles) => {
    this.root.setNativeProps({
      style: styles,
    });
  };

  render() {
    const {
      onHoverText,
      onHoverView,
      textStyle,
      viewStyle,
      ...passThrough
    } = this.props;
    return (
      <Text
        ref={(component) => {
          this.root = component;
        }}
        onMouseEnter={() => this.setStyles(onHoverText)}
        onMouseLeave={() => this.setStyles(textStyle)}
        style={style}
        {...passThrough}
      />
    );
  }
}

export default HoverableText;
